from dataclasses import dataclass, field

@dataclass
class Interval:
    start: int
    end: int
    weight: int
    ind: int
    psum: int = 0

class Solution:
    def maximumWeight(self, intervals: List[List[int]]) -> List[int]:
        n = len(intervals)
        sit = []
        for i in range(n):
            l, r, w = intervals[i]
            sit.append(Interval(start=l, end=r, weight=w, ind=i))

        sit.sort(key=lambda x: (x.end, x.start))
        last = [-1] * n
        etime = [interval.end for interval in sit]
        for i in range(n):
            j = bisect_right(etime, sit[i].start - 1) - 1
            last[i] = j 
        dp = [ [ (0, []) for _ in range(5) ] for _ in range(n+1) ]

        for i in range(1, n+1):
            curr = sit[i-1]
            for k in range(0, 5):
                if k == 0:
                    dp[i][k] = (0, [])
                else:
                    wt1, st1 = dp[i-1][k]
                    if last[i-1] != -1 and k-1 >=0:
                        wtp, stp = dp[last[i-1]+1][k-1]
                    elif last[i-1] == -1 and k-1 >=0:
                        wtp, stp = dp[0][k-1]
                    else:
                        wtp, stp = (0, [])

                    wto = wtp + curr.weight
                    sto = stp + [curr.ind]

                    if wt1 > wto:
                        dp[i][k] = (wt1, st1)
                    elif wt1 < wto:
                        dp[i][k] = (wto, sto)
                    else:
                        sst1 = sorted(st1)
                        sst2 = sorted(sto)
                        if sst1 < sst2:
                            dp[i][k] = (wt1, st1)
                        else:
                            dp[i][k] = (wto, sto)
        bwt = 0
        bst = []
        for k in range(1, 5):
            currwt, currst = dp[n][k]
            if currwt > bwt:
                bwt = currwt
                bst = currst
            elif currwt == bwt:
                if not bst:
                    bst = currst
                else:
                    sbest = sorted(bst)
                    scurr = sorted(currst)
                    if scurr < sbest:
                        bst = currst

        bsrt = sorted(bst)
        return bsrt
