class Solution:
    def zigzagTraversal(self, grid: List[List[int]]) -> List[int]:
        R, C = len(grid), len(grid[0])
        ans = []
        i, j = 0, 0

        def connected(i, j, ii, jj):
            return (i == ii and abs(j - jj) == 1) or (j == jj and abs(i - ii) == 1)

        dir = 1
        while 0 <= i < R and 0 <= j < C:
            ans.append(grid[i][j])
            ii = i
            jj = j + 2 * dir
            if 0 <= jj < C:
                i, j = ii, jj
            else:
                ii = i + 1
                jj = 0 if jj < 0 else C - 1
                dir = -dir
                if 0 <= ii < R and 0 <= jj < C:
                    if connected(i, j, ii, jj):
                        jj += dir
                    i, j = ii, jj
                else:
                    break

        return ans

class Solution:
    def zigzagTraversal(self, grid: List[List[int]]) -> List[int]:

        m, n = len(grid), len(grid[0])

        r = 1
        i, j = 0, 0
        res = []

        while i < m:
            res.append(grid[i][j])
            if r:
                j += 2
                if j >= n:
                    i += 1
                    j = n - 1 - (j - n)
                    r = 1 - r
            else:
                j -= 2
                if j < 0:
                    i += 1
                    j = 0 - j - 1
                    r = 1 - r

        return res

class Solution:
    def zigzagTraversal(self, grid: List[List[int]]) -> List[int]:
        ans = []
        for i, row in enumerate(grid):
            if i % 2:
                ans += row[::-1]
            else:
                ans += row
        return ans[::2]
