class Solution:
    def minMaxSubarraySum(self, nums, k):
        def p(L,R,M):
            u=min(L,M-1)
            if u<=0:return 0
            if R>=M-1:return M*u - u*(u+1)//2
            x=M-R
            if x>u:return R*u
            s=R*(x-1)
            s+=M*(u-x+1)-((u*(u+1))//2-((x-1)*x)//2)
            return s
        def s(a,k,m):
            n=len(a)
            l=[0]*n
            r=[0]*n
            st=[]
            for i in range(n):
                while st and ((a[st[-1]]>a[i]) if m else (a[st[-1]]<a[i])):
                    st.pop()
                l[i]=i-st[-1] if st else i+1
                st.append(i)
            st=[]
            for i in range(n-1,-1,-1):
                while st and ((a[st[-1]]>=a[i]) if m else (a[st[-1]]<=a[i])):
                    st.pop()
                r[i]=st[-1]-i if st else n-i
                st.append(i)
            res=0
            for i in range(n):
                res+=a[i]*p(l[i],r[i],k+1)
            return res
        return s(nums,k,True)+s(nums,k,False)

class lazy_segtree():
    def update(self,k):self.d[k]=self.op(self.d[2*k],self.d[2*k+1])

    def all_apply(self,k,f):
        self.d[k]=self.mapping(f,self.d[k])
        if (k<self.size):self.lz[k]=self.composition(f,self.lz[k])

    def push(self,k):
        self.all_apply(2*k,self.lz[k])
        self.all_apply(2*k+1,self.lz[k])
        self.lz[k]=self.identity

    def __init__(self,V,OP,E,MAPPING,COMPOSITION,ID):
        self.n=len(V)
        self.log=(self.n-1).bit_length()
        self.size=1<<self.log
        self.d=[E for i in range(2*self.size)]
        self.lz=[ID for i in range(self.size)]
        self.e=E
        self.op=OP
        self.mapping=MAPPING
        self.composition=COMPOSITION
        self.identity=ID
        for i in range(self.n):self.d[self.size+i]=V[i]
        for i in range(self.size-1,0,-1):self.update(i)

    def set(self,p,x):
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        self.d[p]=x
        for i in range(1,self.log+1):self.update(p>>i)

    def get(self,p):
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        return self.d[p]

    def prod(self,l,r):
        if l==r:return self.e
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self.push(l>>i)
            if (((r>>i)<<i)!=r):self.push(r>>i)
        sml,smr=self.e,self.e
        while(l<r):
            if l&1:
                sml=self.op(sml,self.d[l])
                l+=1
            if r&1:
                r-=1
                smr=self.op(self.d[r],smr)
            l>>=1
            r>>=1
        return self.op(sml,smr)

    def all_prod(self):return self.d[1]

    def apply_point(self,p,f):
        assert 0<=p and p<self.n
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        self.d[p]=self.mapping(f,self.d[p])
        for i in range(1,self.log+1):self.update(p>>i)

    def apply(self,l,r,f):
        if l==r:return
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self.push(l>>i)
            if (((r>>i)<<i)!=r):self.push((r-1)>>i)
        l2,r2=l,r
        while(l<r):
            if (l&1):
                self.all_apply(l,f)
                l+=1
            if (r&1):
                r-=1
                self.all_apply(r,f)
            l>>=1
            r>>=1
        l,r=l2,r2
        for i in range(1,self.log+1):
            if (((l>>i)<<i)!=l):self.update(l>>i)
            if (((r>>i)<<i)!=r):self.update((r-1)>>i)

    def max_right(self,l,g):
        if l==self.n:return self.n
        l+=self.size
        for i in range(self.log,0,-1):self.push(l>>i)
        sm=self.e
        while(1):
            while(l%2==0):l>>=1
            if not(g(self.op(sm,self.d[l]))):
                while(l<self.size):
                    self.push(l)
                    l=(2*l)
                    if (g(self.op(sm,self.d[l]))):
                        sm=self.op(sm,self.d[l])
                        l+=1
                return l-self.size
            sm=self.op(sm,self.d[l])
            l+=1
            if (l&-l)==l:break
        return self.n

    def min_left(self,r,g):
        if r==0:return 0
        r+=self.size
        for i in range(self.log,0,-1):self.push((r-1)>>i)
        sm=self.e
        while(1):
            r-=1
            while(r>1 and (r%2)):r>>=1
            if not(g(self.op(self.d[r],sm))):
                while(r<self.size):
                    self.push(r)
                    r=(2*r+1)
                    if g(self.op(self.d[r],sm)):
                        sm=self.op(self.d[r],sm)
                        r-=1
                return r+1-self.size
            sm=self.op(self.d[r],sm)
            if (r&-r)==r:break
        return 0

def is_prime(n):
    if n <= 1:
        return False
    if n == 2 or n == 7 or n == 61:
        return True
    if n % 2 == 0:
        return False
    d = n - 1
    while d % 2 == 0:
        d //= 2
    bases = [2, 7, 61]
    for a in bases:
        t = d
        y = pow_mod(a, t, n)
        while t != n - 1 and y != 1 and y != n - 1:
            y = y * y % n
            t <<= 1
        if y != n - 1 and t % 2 == 0:
            return False
    return True

def primitive_root(m):
    if m == 2:
        return 1
    if m in (167772161, 469762049, 754974721, 998244353):
        return 3
    divs = [2]
    x = (m - 1) // 2
    while x % 2 == 0:
        x //= 2
    i = 3
    while i * i <= x:
        if x % i == 0:
            divs.append(i)
            while x % i == 0:
                x //= i
        i += 2
    if x > 1:
        divs.append(x)

    g = 2
    while True:
        ok = True
        for d in divs:
            if pow_mod(g, (m - 1) // d, m) == 1:
                ok = False
                break
        if ok:
            return g
        g += 1

def zs(s):
    n = len(s)
    ret = [0] * n
    l = 1
    ll = 0
    r = 0
    while l < len(s):
        if l < r:
            ret[l] = min(ret[l-ll],r-l)

        i = ret[l]
        while l+i < len(s) and s[i] == s[l+i]:
            ret[l] +=1

            i +=1
        if ret[l] >0:
            if l+i-1 > r:
                ll = l
                r = max(r,l+i-1)        
        l+=1
    return ret

class Solution:
    def minMaxSubarraySum(self, arr: List[int], k: int) -> int:
        n = len(arr)

        prev1 = [-1] * n
        nx1 = [n] * n
        prev2 = [-1] * n
        nx2 = [n] * n

        stk = []
        for i in range(n):
            while stk and arr[stk[-1]] >= arr[i]:
                stk.pop()
            prev1[i] = stk[-1] if stk else -1
            stk.append(i)

        stk = []
        for i in range(n - 1, -1, -1):
            while stk and arr[stk[-1]] > arr[i]:
                stk.pop()
            nx1[i] = stk[-1] if stk else n
            stk.append(i)

        stk = []
        for i in range(n):
            while stk and arr[stk[-1]] <= arr[i]:
                stk.pop()
            prev2[i] = stk[-1] if stk else -1
            stk.append(i)

        stk = []
        for i in range(n - 1, -1, -1):
            while stk and arr[stk[-1]] < arr[i]:
                stk.pop()
            nx2[i] = stk[-1] if stk else n
            stk.append(i)

        tot = 0

        def calc(i, left, right, val):
            L = left
            R = right
            lim = min(L, k)
            if lim <= 0:
                return 0
            sumc = 0
            cng = max(1, k - R + 1)
            if cng > lim:
                sumc = lim * R
            else:
                fp = cng - 1
                if fp > 0:
                    sumc += fp * R
                for x in range(max(1, cng), lim + 1):
                    sumc += (k - x + 1)
            return val * sumc

        for i in range(n):
            lmin = i - prev1[i]
            rmin = nx1[i] - i
            tot += calc(i, lmin, rmin, arr[i])

            lmax = i - prev2[i]
            rmax = nx2[i] - i
            tot += calc(i, lmax, rmax, arr[i])

        return tot
