    def minCost(self, n: int, cost: List[List[int]]) -> int:
        dp = [[[float('inf')] * 3 for _ in range(3)] for __ in range(n//2+1)]
        for cl in range(3):
            for cr in range(3):
                dp[0][cl][cr] = 0

        for nl in range(3):
            for nr in range(3):
                if nl != nr:
                    for cl in range(3):
                        for cr in range(3):
                            v = dp[0][cl][cr] + cost[0][nl] + cost[n-1][nr]
                            if v < dp[1][nl][nr]:
                                dp[1][nl][nr] = v

        for i in range(1, n//2):
            for cl in range(3):
                for cr in range(3):
                    p = dp[i][cl][cr]
                    if p == inf:
                        continue
                    for nl in range(3):
                        for nr in range(3):
                            if nl != nr and nl != cl and nr != cr:
                                v = p + cost[i][nl] + cost[n-1-i][nr]
                                if v < dp[i+1][nl][nr]:
                                    dp[i+1][nl][nr] = v

        r = float('inf')

        for cl in range(3):
            for cr in range(3):
                if dp[n//2][cl][cr] < r:
                    r = dp[n//2][cl][cr]
        return r
