from math import gcd


class Solution:
    def minimumIncrements(self, nums: List[int], target: List[int]) -> int:
        m = len(target)
        full = (1 << m) - 1
        subs = []
        for s in range(1, 1 << m):
            lcm_val = 1
            for i in range(m):
                if s >> i & 1:
                    lcm_val = lcm_val * target[i] // gcd(lcm_val, target[i])
            subs.append((s, lcm_val))
        INF = 10**19
        dp = [INF] * (1 << m)
        dp[0] = 0
        for a in nums:
            ndp = dp[:]
            for mask in range(1 << m):
                if dp[mask] == INF:
                    continue
                for s, l in subs:
                    nm = mask | s
                    cost = (l - (a % l)) % l
                    ndp[nm] = min(ndp[nm], dp[mask] + cost)
            dp = ndp
        return dp[full]

