class MinIncrementsSolver:
    def lcm(self, a, b):
        return a // gcd(a, b) * b

    def unique_targets(self, target):
        return list(set(sorted(target)))

    def generate_candidates(self, target):
        m = len(target)
        candidates = []
        seen = set()
        total = 1 << m
        for s in range(1, total):
            curL = 1
            for j in range(m):
                if s & (1 << j):
                    curL = self.lcm(curL, target[j])
            cover_mask = 0
            for j in range(m):
                if curL % target[j] == 0:
                    cover_mask |= (1 << j)
            if curL not in seen:
                seen.add(curL)
                candidates.append(Candidate(curL, cover_mask))
        return candidates

    def compute_cost(self, n, L):
        rem = n % L
        return 0 if rem == 0 else (L - rem)

    def solve_dp(self, nums, candidates, full_mask, m):
        INF = 10**18
        dp = [INF] * (1 << m)
        dp[0] = 0
        for num in nums:
            new_dp = dp[:]
            for cand in candidates:
                cost = self.compute_cost(num, cand.L)
                for mask in range(1 << m):
                    if dp[mask] == INF:
                        continue
                    nmask = mask | cand.mask
                    new_dp[nmask] = min(new_dp[nmask], dp[mask] + cost)
            dp = new_dp
        return dp[full_mask]

    def minimum_increments(self, nums, target):
        uniq = self.unique_targets(target)
        m = len(uniq)
        full_mask = (1 << m) - 1
        candidates = self.generate_candidates(uniq)
        ans = self.solve_dp(nums, candidates, full_mask, m)
        return ans

class Solution:
    def minimumIncrements(self, nums, target):
        solver = MinIncrementsSolver()
        result = solver.minimum_increments(nums, target)
        return int(result)

