class Solution:
    def minimumIncrements(self, nums: List[int], target: List[int]) -> int:
        cpy = nums[:]
        n = len(cpy)
        m = len(target)
        fmsk = (1 << m) - 1
        mlcm = [1] * (1 << m)
        for msks in range(1, 1 << m):
            clcm = 1
            for j in range(m):
                if msks & (1 << j):
                    clcm = (clcm * target[j]) // math.gcd(clcm, target[j])
            mlcm[msks] = clcm

        INF = float('inf')
        dp = [[INF] * (1 << m) for i in range(n + 1)]
        dp[0][0] = 0

        for i in range(n):
            x = cpy[i]
            ls = []
            for msks in range(1, 1 << m):
                z = mlcm[msks]
                mulx = (x + z - 1) // z 
                nval = mulx * z
                cstx = nval - x
                ls.append((msks, cstx))

            for msks in range(1 << m):
                dp[i + 1][msks] = min(dp[i + 1][msks], dp[i][msks])
                for smsk, cstx in ls:
                    mskn = msks | smsk
                    dp[i + 1][mskn] = min(dp[i + 1][mskn], dp[i][msks] + cstx)

        return dp[n][fmsk]
