class Solution:
    def sortMatrix(self, grid: List[List[int]]) -> List[List[int]]:
        n = len(grid)
        x = 0
        y = n - 1
        while True:
            pos = []
            cur = 0
            while x + cur < n and y + cur < n:
                pos.append(grid[x + cur][y + cur])
                cur += 1
            cur = 0
            if y > 0:
                pos.sort()
            else:
                pos.sort(reverse=True)
            while x + cur < n and y + cur < n:
                grid[x + cur][y + cur] = pos[cur]
                cur += 1



            if y > 0:
                y -= 1
            elif x < n - 1:
                x += 1
            else:
                break
        return grid

