class Solution:
    def sortMatrix(self, grid: List[List[int]]) -> List[List[int]]:
        n = len(grid)
        c = defaultdict(list)
        for i in range(n):
            for j in range(n):
                c[i - j].append(grid[i][j])
        for k in c.keys():
            if k >= 0:
                c[k].sort()
            else:
                c[k].sort(reverse=True)

        for i in range(n):
            for j in range(n):
                f = i - j
                grid[i][j] = c[f][-1]
                c[f].pop()

        return grid
