class Solution:
    def hasSameDigits(self, s: str) -> bool:
        n = len(s)
        if n == 2:
            return (s[0] == s[1])

        digits = list(map(int, s))
        N = n - 2

        binom_mod5 = [[0]*5 for _ in range(5)]
        for i in range(5):
            binom_mod5[i][0] = 1
            for j in range(1, i+1):
                from math import comb
                binom_mod5[i][j] = comb(i, j) % 5

        combine_map = {}
        for x in range(10):
            pair = (x % 2, x % 5)
            combine_map[pair] = x

        def binom_mod2(n, k):
            return 1 if (k & ~n) == 0 else 0

        def binom_mod5_func(n, k):
            res = 1
            while n > 0 or k > 0:
                n5 = n % 5
                k5 = k % 5
                if k5 > n5:
                    return 0
                res = (res * binom_mod5[n5][k5]) % 5
                n //= 5
                k //= 5
            return res

        def binom_mod10(n, k):
            b2 = binom_mod2(n, k)
            b5 = binom_mod5_func(n, k)
            return combine_map[(b2, b5)]

        binom_table = [0]*(N+1)
        for k in range(N+1):
            binom_table[k] = binom_mod10(N, k)
        d0 = 0
        d1 = 0
        for m in range(N+1):
            c = binom_table[m]
            d0 = (d0 + digits[m]*c) % 10
            d1 = (d1 + digits[m+1]*c) % 10    
        return (d0 == d1)

class Solution:
    def hasSameDigits(self, s: str) -> bool:
        while len(s)!=2:
            res=""
            for i in range(1,len(s)):
                res+=str(int(s[i])+int(s[i-1]))[-1]
            s=res
        return len(set(s))==1

class Solution:
    def hasSameDigits(self, s: str) -> bool:
        while len(s) > 2:
            s = "".join(
                str((int(s[i]) + int(s[i + 1])) % 10) for i in range(len(s) - 1)
            )
        return s[0] == s[1]
