def is_prime(n):
    if n <= 1:
        return False
    if n == 2 or n == 7 or n == 61:
        return True
    if n % 2 == 0:
        return False
    d = n - 1
    while d % 2 == 0:
        d //= 2
    bases = [2, 7, 61]
    for a in bases:
        t = d
        y = pow_mod(a, t, n)
        while t != n - 1 and y != 1 and y != n - 1:
            y = y * y % n
            t <<= 1
        if y != n - 1 and t % 2 == 0:
            return False
    return True

def primitive_root(m):
    if m == 2:
        return 1
    if m in (167772161, 469762049, 754974721, 998244353):
        return 3
    divs = [2]
    x = (m - 1) // 2
    while x % 2 == 0:
        x //= 2
    i = 3
    while i * i <= x:
        if x % i == 0:
            divs.append(i)
            while x % i == 0:
                x //= i
        i += 2
    if x > 1:
        divs.append(x)

    g = 2
    while True:
        ok = True
        for d in divs:
            if pow_mod(g, (m - 1) // d, m) == 1:
                ok = False
                break
        if ok:
            return g
        g += 1

def zs(s):
    n = len(s)
    ret = [0] * n
    l = 1
    ll = 0
    r = 0
    while l < len(s):
        if l < r:
            ret[l] = min(ret[l-ll],r-l)

        i = ret[l]
        while l+i < len(s) and s[i] == s[l+i]:
            ret[l] +=1

            i +=1
        if ret[l] >0:
            if l+i-1 > r:
                ll = l
                r = max(r,l+i-1)        
        l+=1
    return ret

class Solution:
    def generateString(self, str1: str, str2: str) -> str:
        n, m, L = len(str1), len(str2), len(str1) + len(str2) - 1
        ans = ['?'] * L
        flag = [False] * L

        if not self.applys(str1, str2, ans, flag):
            return ""

        self.fills(ans)

        if not self.applyv(str1, str2, ans, flag):
            return ""

        if not self.answer(str1, str2, ans):
            return ""

        return ''.join(ans)

    def applys(self, str1, str2, ans, flag):
        n, m = len(str1), len(str2)
        for i in range(n):
            if str1[i] == 'T':
                for j in range(m):
                    pos = i + j
                    if ans[pos] == '?':
                        ans[pos] = str2[j]
                        flag[pos] = True
                    elif ans[pos] != str2[j]:
                        return False
        return True

    def fills(self, ans):
        for i in range(len(ans)):
            if ans[i] == '?':
                ans[i] = 'a'

    def applyv(self, str1, str2, ans, flag):
        n, m = len(str1), len(str2)
        for i in range(n):
            if str1[i] == 'F':
                diff = any(ans[i + j] != str2[j] for j in range(m))
                if not diff:
                    ok = False
                    for j in range(m - 1, -1, -1):
                        pos = i + j
                        if not flag[pos]:
                            ps = ans[pos]
                            z = 'b' if str2[j] == 'a' else 'a'
                            if z <= ps:
                                z = chr(ord(ps) + 1)
                            ans[pos] = z
                            ok = True
                            break
                    if not ok:
                        return False
        return True

    def answer(self, str1, str2, ans):
        n, m = len(str1), len(str2)
        for i in range(n):
            if str1[i] == 'T':
                if any(ans[i + j] != str2[j] for j in range(m)):
                    return False
        return True

class Solution:
    def generateString(self, str1: str, str2: str) -> str:
        n, m = len(str1), len(str2)
        L = n + m - 1
        word = [None] * L
        fixed = [False] * L

        for i in range(n):
            if str1[i] == 'T':
                for j in range(m):
                    pos = i + j
                    if word[pos] is not None and word[pos] != str2[j]:
                        return ""
                    word[pos] = str2[j]
                    fixed[pos] = True

        for i in range(L):
            if word[i] is None:
                word[i] = 'a'

        for i in range(n):
            if str1[i] == 'F':
                equal = True
                for j in range(m):
                    if word[i+j] != str2[j]:
                        equal = False
                        break
                if equal:
                    modified = False
                    for pos in range(i + m - 1, i - 1, -1):
                        if not fixed[pos]:
                            word[pos] = 'b'
                            modified = True
                            break
                    if not modified:
                        return ""
        return "".join(word)

class Solution:
    def generateString(self, str1: str, str2: str) -> str:

        n, m = len(str1), len(str2)
        L = n + m - 1
        res = [None] * L
        free = [True] * L

        for i in range(n):
            if str1[i] == 'T':
                for j in range(m):
                    pos = i + j
                    c = str2[j]
                    if res[pos] is None:
                        res[pos] = c
                        free[pos] = False
                    elif res[pos] != c:
                        return ""

        for i in range(L):
            if res[i] is None:
                res[i] = 'a'

        for i in range(n):
            if str1[i] == 'F':
                eq = True

                for j in range(m):
                    if res[i+j] != str2[j]:
                        eq = False
                        break

                if eq:
                    cand = -1
                    for j in range(m):
                        pos = i + j
                        if free[pos]:
                            cand = pos
                    if cand == -1:
                        return ""
                    res[cand] = 'b'
                    free[cand] = False

        return "".join(res)
