class Solution:
    def findMaxSum(self, a: List[int], b: List[int], k: int) -> List[int]:
        n = len(a)
        P = sorted(zip(a, b, range(n)), key=lambda x: x[0])
        R = [0] * n
        h, s = [], 0
        i = 0
        while i < n:
            j = i
            while j < n and P[j][0] == P[i][0]:
                R[P[j][2]] = s
                j += 1
            for _, y, _ in P[i:j]:
                if len(h) < k:
                    heapq.heappush(h, y)
                    s += y
                elif y > h[0]:
                    s += y - h[0]
                    heapq.heapreplace(h, y)
            i = j
        return R
