class Solution:
    def numOfUnplacedFruits(self, f: List[int], b: List[int]) -> int:
        n = len(f)
        u = [0]*n
        c = 0
        for x in f:
            p = 0
            for i in range(n):
                if not u[i] and b[i] >= x:
                    u[i] = 1
                    p = 1
                    break
            if not p:
                c += 1
        return c

class Solution:
    def numOfUnplacedFruits(self, fruits: List[int], baskets: List[int]) -> int:
        n = len(fruits)

        usedBaskets = [False] * n
        placedFruits = [False] * n

        for i in range(n):
            fruitQty = fruits[i]

            leftmostBasket = -1
            for j in range(n):
                if not usedBaskets[j] and baskets[j] >= fruitQty:
                    leftmostBasket = j
                    break

            if leftmostBasket != -1:
                placedFruits[i] = True
                usedBaskets[leftmostBasket] = True

        unplacedCount = sum(1 for placed in placedFruits if not placed)
        return unplacedCount

class Solution:
    def numOfUnplacedFruits(self, a: List[int], b: List[int]) -> int:
        n = len(a)
        m = 1 << ((n - 1).bit_length())

        t = [-1] * (2 * m)
        for i in range(n):
            t[m + i] = b[i]

        for i in range(m - 1, 0, -1):
            t[i] = max(t[2 * i], t[2 * i + 1])
        def u(i, v):
            i += m
            t[i] = v
            i //= 2
            while i:
                t[i] = max(t[2 * i], t[2 * i + 1])
                i //= 2
        def q(x):
            if t[1] < x:
                return -1
            i = 1
            while i < m:
                i = 2 * i if t[2 * i] >= x else 2 * i + 1
            return i - m if i - m < n else -1
        ans = 0
        for x in a:
            i = q(x)
            if i == -1:
                ans += 1
            else:
                u(i, -1)
        return ans
