class Solution:
    def maxSum(self, nums: List[int]) -> int:
        positive_set = {num for num in nums if num > 0}

        if positive_set:
            return sum(positive_set)
        else:
            return max(nums)

class Solution:
    def maxSum(self, nums: List[int]) -> int:
        positive_set = set()
        total_sum = 0
        for num in nums:
            if num > 0 and num not in positive_set:
                positive_set.add(num)
                total_sum += num
        if total_sum > 0:
            return total_sum
        return max(nums)
