    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        a = nums
        n = len(a)
        if all(x==0 for x in a):
            return 0
        d = [1]*n
        for i, q in enumerate(queries):
            x, y, z = q
            for j in range(x, y+1):
                d[j] |= d[j] << z
            if all(d[j] & (1 << a[j]) for j in range(n)):
                return i+1
        return -1

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        total_queries = len(queries)

        def can_sum_to(vals: List[int], target: int) -> bool:
            dp = [False] * (target + 1)
            dp[0] = True
            for v in vals:
                for t in range(target, v - 1, -1):
                    if dp[t - v]:
                        dp[t] = True
                if dp[target]:
                    return True
            return dp[target]

        for k in range(total_queries + 1):
            possible_for_all = True
            for i in range(n):
                if nums[i] == 0:
                    continue
                available_vals = []
                for j in range(k):
                    l, r, val = queries[j]
                    if l <= i <= r:
                        available_vals.append(val)
                if not can_sum_to(available_vals, nums[i]):
                    possible_for_all = False
                    break

            if possible_for_all:
                return k

        return -1

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if all(num == 0 for num in nums):
            return 0
        def is_feasible(num_queries):
            for index in range(n):
                required = nums[index]
                dp = 1
                mask = (1 << (required + 1)) - 1
                for i in range(num_queries):
                    left_bound, right_bound, query_value = queries[i]
                    if left_bound <= index <= right_bound:
                        dp = dp | (dp << query_value)
                        dp &= mask
                if (dp >> required) & 1 == 0:
                    return False
            return True
        low = 0
        high = len(queries)
        answer = -1
        while low <= high:
            mid = (low + high) // 2
            if is_feasible(mid):
                answer = mid
                high = mid - 1
            else:
                low = mid + 1
        return answer
