class Solution:
    def minTime(self, s, m):
        n = len(s)
        f = [0] * n
        for x in m:
            nw = f[0]
            for i in range(1, n):
                nw = max(nw + s[i - 1] * x, f[i])
            f[n - 1] = nw + s[n - 1] * x
            for i in range(n - 2, -1, -1):
                f[i] = f[i + 1] - s[i + 1] * x
        return f[n - 1]

class Solution:
    def minTime(self, skill: List[int], mana: List[int]) -> int:
        n, m = len(skill), len(mana)
        ss = [0] * n
        ss[0] = skill[0]
        for i in range(1, n):
            ss[i] = ss[i - 1] + skill[i]
        S = 0
        for j in range(1, m):
            delay = skill[0] * mana[j - 1]
            for i in range(1, n):
                d = ss[i] * mana[j - 1] - ss[i - 1] * mana[j]
                if d > delay:
                    delay = d
            S += delay
        return S + ss[-1] * mana[-1]

class Solution:
    def minTime(self, skill: List[int], mana: List[int]) -> int:
        n, m = len(skill), len(mana)
        dp = [0] * (n + 1)
        for i in range(1, n + 1):
            dp[i] = dp[i - 1] + skill[i - 1]
        s = 0
        for j in range(m - 1):
            s += max(mana[j] * dp[i] - mana[j + 1] * dp[i - 1] for i in range(1, n + 1))
        return mana[m - 1] * dp[n] + s
