        res = a
        for i in range(1, len(res)):
            res[i] = min(res[i], res[i - 1])
        return res

class Solution:
    def minCosts(self, cost: List[int]) -> List[int]:
        for i in range(1, len(cost)):
            if cost[i] > cost[i-1]:
                cost[i] = cost[i-1]
        return cost

class Solution:
    def minCosts(self, cost: List[int]) -> List[int]:
        prev = float("inf")
        ans = []
        for i, c in enumerate(cost):
            prev = min(prev, c)
            ans.append(prev)
        return ans
