from collections import defaultdict
from typing import List

class Solution:
    def maxProduct(self, a: List[int], b: int, c: int) -> int:
        a = (a.copy(), b, c)
        d = float('inf')
        e = defaultdict(set)

        i = 0
        while i < len(a[0]):
            f = defaultdict(set)
            g = a[0][i]
            for h in e:
                f[h] |= e[h]

            if g == 0:
                f[(1, 0)].add(0)
            else:
                if g <= c:
                    f[(1, g)].add(g)
                else:
                    f[(1, d)].add(g)

            j = list(e.keys())
            k = 0
            while k < len(j):
                (l, m) = j[k]
                for n in e[(l, m)]:
                    o = 1 - l
                    p = n + g if l == 0 else n - g
                    if g == 0:
                        q = 0
                    else:
                        if m == d:
                            q = d
                        else:
                            r = m * g
                            q = r if r <= c else d
                    f[(o, q)].add(p)
                k += 1

            e = f
            i += 1

        s = -1
        j = list(e.keys())
        t = 0
        while t < len(j):
            (l, m) = j[t]
            if m != d and b in e[(l, m)]:
                s = max(s, m)
            t += 1

        return s

class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        dp = set([(0, 0, 1, True)])
        for num in nums:
            newdp = []
            for s, f, p, flag in dp:
                if f == 0:
                    news = s + num
                else:
                    news = s - num
                newf = 1 - f
                newp = p * num
                if newp > limit:
                    newp = limit + 1
                newdp.append((news, newf, newp, False))
            for t in newdp:
                dp.add(t)
        ans = -1
        # print(dp)
        for s, f, p, flag in dp:
            if flag == False and s == k and p <= limit:
                ans = max(ans, p)
        return ans

class lazy_segtree():
    def update(self,k):self.d[k]=self.op(self.d[2*k],self.d[2*k+1])

    def all_apply(self,k,f):
        self.d[k]=self.mapping(f,self.d[k])
        if (k<self.size):self.lz[k]=self.composition(f,self.lz[k])

    def push(self,k):
        self.all_apply(2*k,self.lz[k])
        self.all_apply(2*k+1,self.lz[k])
        self.lz[k]=self.identity

    def __init__(self,V,OP,E,MAPPING,COMPOSITION,ID):
        self.n=len(V)
        self.log=(self.n-1).bit_length()
        self.size=1<<self.log
        self.d=[E for i in range(2*self.size)]
        self.lz=[ID for i in range(self.size)]
        self.e=E
        self.op=OP
        self.mapping=MAPPING
        self.composition=COMPOSITION
        self.identity=ID
        for i in range(self.n):self.d[self.size+i]=V[i]
        for i in range(self.size-1,0,-1):self.update(i)

    def set(self,p,x):
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        self.d[p]=x
        for i in range(1,self.log+1):self.update(p>>i)

    def get(self,p):
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        return self.d[p]

    def prod(self,l,r):
        if l==r:return self.e
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self.push(l>>i)
            if (((r>>i)<<i)!=r):self.push(r>>i)
        sml,smr=self.e,self.e
        while(l<r):
            if l&1:
                sml=self.op(sml,self.d[l])
                l+=1
            if r&1:
                r-=1
                smr=self.op(self.d[r],smr)
            l>>=1
            r>>=1
        return self.op(sml,smr)

    def all_prod(self):return self.d[1]

    def apply_point(self,p,f):
        assert 0<=p and p<self.n
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        self.d[p]=self.mapping(f,self.d[p])
        for i in range(1,self.log+1):self.update(p>>i)

    def apply(self,l,r,f):
        if l==r:return
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self.push(l>>i)
            if (((r>>i)<<i)!=r):self.push((r-1)>>i)
        l2,r2=l,r
        while(l<r):
            if (l&1):
                self.all_apply(l,f)
                l+=1
            if (r&1):
                r-=1
                self.all_apply(r,f)
            l>>=1
            r>>=1
        l,r=l2,r2
        for i in range(1,self.log+1):
            if (((l>>i)<<i)!=l):self.update(l>>i)
            if (((r>>i)<<i)!=r):self.update((r-1)>>i)

    def max_right(self,l,g):
        if l==self.n:return self.n
        l+=self.size
        for i in range(self.log,0,-1):self.push(l>>i)
        sm=self.e
        while(1):
            while(l%2==0):l>>=1
            if not(g(self.op(sm,self.d[l]))):
                while(l<self.size):
                    self.push(l)
                    l=(2*l)
                    if (g(self.op(sm,self.d[l]))):
                        sm=self.op(sm,self.d[l])
                        l+=1
                return l-self.size
            sm=self.op(sm,self.d[l])
            l+=1
            if (l&-l)==l:break
        return self.n

    def min_left(self,r,g):
        if r==0:return 0
        r+=self.size
        for i in range(self.log,0,-1):self.push((r-1)>>i)
        sm=self.e
        while(1):
            r-=1
            while(r>1 and (r%2)):r>>=1
            if not(g(self.op(self.d[r],sm))):
                while(r<self.size):
                    self.push(r)
                    r=(2*r+1)
                    if g(self.op(self.d[r],sm)):
                        sm=self.op(self.d[r],sm)
                        r-=1
                return r+1-self.size
            sm=self.op(self.d[r],sm)
            if (r&-r)==r:break
        return 0

def is_prime(n):
    if n <= 1:
        return False
    if n == 2 or n == 7 or n == 61:
        return True
    if n % 2 == 0:
        return False
    d = n - 1
    while d % 2 == 0:
        d //= 2
    bases = [2, 7, 61]
    for a in bases:
        t = d
        y = pow_mod(a, t, n)
        while t != n - 1 and y != 1 and y != n - 1:
            y = y * y % n
            t <<= 1
        if y != n - 1 and t % 2 == 0:
            return False
    return True

def primitive_root(m):
    if m == 2:
        return 1
    if m in (167772161, 469762049, 754974721, 998244353):
        return 3
    divs = [2]
    x = (m - 1) // 2
    while x % 2 == 0:
        x //= 2
    i = 3
    while i * i <= x:
        if x % i == 0:
            divs.append(i)
            while x % i == 0:
                x //= i
        i += 2
    if x > 1:
        divs.append(x)

    g = 2
    while True:
        ok = True
        for d in divs:
            if pow_mod(g, (m - 1) // d, m) == 1:
                ok = False
                break
        if ok:
            return g
        g += 1

def zs(s):
    n = len(s)
    ret = [0] * n
    l = 1
    ll = 0
    r = 0
    while l < len(s):
        if l < r:
            ret[l] = min(ret[l-ll],r-l)

        i = ret[l]
        while l+i < len(s) and s[i] == s[l+i]:
            ret[l] +=1

            i +=1
        if ret[l] >0:
            if l+i-1 > r:
                ll = l
                r = max(r,l+i-1)        
        l+=1
    return ret

from collections import defaultdict
from typing import List, Dict, Set, Tuple

def cdp(dp: Dict[Tuple[int, int], Set[int]]) -> Dict[Tuple[int, int], Set[int]]:
    ndp = defaultdict(set)
    for state, ast in dp.items():
        ndp[state] |= ast
    return ndp

def updates(ndp: Dict[Tuple[int, int], Set[int]], x: int, limit: int, INF: float) -> None:
    if x == 0:
        ndp[(1, 0)].add(0)
    else:
        if x <= limit:
            ndp[(1, x)].add(x)
        else:
            ndp[(1, INF)].add(x)

def updates2(ndp: Dict[Tuple[int, int], Set[int]], dp: Dict[Tuple[int, int], Set[int]], x: int, limit: int, INF: float) -> None:
    for (parity, prod), ast in dp.items():
        for alt in ast:
            npp = 1 - parity
            na = alt + x if parity == 0 else alt - x
            if x == 0:
                prodn = 0
            else:
                if prod == INF:
                    prodn = INF
                else:
                    cd = prod * x
                    prodn = cd if cd <= limit else INF
            ndp[(npp, prodn)].add(na)

def solve(nums: List[int], limit: int, INF: float) -> Dict[Tuple[int, int], Set[int]]:
    dp = defaultdict(set)
    for x in nums:
        ndp = cdp(dp)
        updates(ndp, x, limit, INF)
        updates2(ndp, dp, x, limit, INF)
        dp = ndp
    return dp

class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        INF = float('inf')

        dp = solve(nums, limit, INF)

        best = -1
        for (parity, prod), ast in dp.items():
            if prod != INF and k in ast:
                best = max(best, prod)
        return best
