class Solution:
    def smallestPalindrome(self, s: str) -> str:
        freq = [0] * 26
        for ch in s:
            freq[ord(ch) - ord('a')] += 1

        center = ""
        for i in range(26):
            if freq[i] % 2 == 1:
                center = chr(i + ord('a'))
                freq[i] -= 1
                break

        first_half = []
        for i in range(26):
            first_half.append(chr(i + ord('a')) * (freq[i] // 2))

        first_half_str = "".join(first_half)
        result = first_half_str + center + first_half_str[::-1]
        return result

class Solution:
    def smallestPalindrome(self, s: str) -> str:
        charCount = {}
        for char in s:
            charCount[char] = charCount.get(char, 0) + 1

        oddChar = None
        for char, count in charCount.items():
            if count % 2 == 1:
                if oddChar is None or char < oddChar:
                    oddChar = char

        leftHalf = []
        for char in sorted(charCount.keys()):
            if char == oddChar:
                leftHalf.extend([char] * ((charCount[char] - 1) // 2))
            else:
                leftHalf.extend([char] * (charCount[char] // 2))

        middle = [oddChar] if oddChar is not None else []

        rightHalf = leftHalf.copy()
        rightHalf.reverse()

        return ''.join(leftHalf + middle + rightHalf)

class Solution:
    def smallestPalindrome(self, s: str) -> str:
        c = Counter(s)
        arr = []
        mid = ""
        for x in sorted(c):
            n = c[x] // 2
            arr.append(x * n)
            if c[x] % 2:
                mid = x
        left = "".join(arr)
        return left + mid + left[::-1]
