class Solution:
    def calculateScore(self, instructions: List[str], values: List[int]) -> int:
        n = len(instructions)
        visited = set()
        score = 0
        i = 0

        while 0 <= i < n:
            if i in visited:
                break

            visited.add(i)
            if instructions[i] == "add":
                score += values[i]
                i += 1
            elif instructions[i] == "jump":
                i = i + values[i]

        return score
