        return len(a)

class Solution:
    def maximumPossibleSize(self, nums: List[int]) -> int:
        res = 0
        mx = float('-inf')
        for el in nums:
            if el >= mx:
                mx = el
                res += 1
        return res

class Solution:
    def maximumPossibleSize(self, nums: List[int]) -> int:
        stack = []
        for currVal in nums:
            stack.append(currVal)
            while len(stack) > 1 and stack[-2] > stack[-1]:
                lastVal = stack.pop()
                prevVal = stack.pop()
                stack.append(lastVal if lastVal > prevVal else prevVal)
        return len(stack)
