    def resultArray(self, nums: List[int], k: int) -> List[int]:
        res = [0] * k
        prev = [0] * k

        for n in nums:
            m = n % k
            cur = [0] * k
            cur[m] += 1
            for r in range(k):
                if prev[r]:
                    new_r = (r * m) % k
                    cur[new_r] += prev[r]
            for r in range(k):
                res[r] += cur[r]
            prev = cur

        return res

