    def countCoveredBuildings(self, n: int, buildings: List[List[int]]) -> int:
        mn1 = [float('inf')] * (n + 1)
        mx1 = [-float('inf')] * (n + 1)
        mn2 = [float('inf')] * (n + 1)
        mx2 = [-float('inf')] * (n + 1)

        for b in buildings:
            x, y = b[0], b[1]
            mn1[x] = min(mn1[x], y)
            mx1[x] = max(mx1[x], y)
            mn2[y] = min(mn2[y], x)
            mx2[y] = max(mx2[y], x)

        ans = 0
        for b in buildings:
            x, y = b[0], b[1]
            left  = y > mn1[x]
            right = y < mx1[x]
            upp    = x > mn2[y]
            down  = x < mx2[y]
            if left and right and upp and down:
                ans += 1

        return ans

