class Solution:

    @lru_cache(None)
    def specialGrid(self, N: int) -> List[List[int]]:
        if N == 0:
            return [[0]]
        M = 1 << (N - 1)
        G = self.specialGrid(N - 1)
        A = M * M
        ans = [[0] * (M * 2) for _ in range(M * 2)]
        for i in range(M):
            for j in range(M):
                r = G[i][j]
                ans[i][j + M] = r
                ans[i + M][j + M] = r + A
                ans[i + M][j] = r + 2 * A
                # print(f"i={i}, j={j}, r={r}, A={A}, M={M}")
                ans[i][j] = r + 3 * A
                # print(f"i={i}, j={j}, r={r}, A={A}, M={M}")
        # print(f"ans={ans}")
        return ans
