class lazy_segtree():
    def update(self,k):self.d[k]=self.op(self.d[2*k],self.d[2*k+1])

    def all_apply(self,k,f):
        self.d[k]=self.mapping(f,self.d[k])
        if (k<self.size):self.lz[k]=self.composition(f,self.lz[k])

    def push(self,k):
        self.all_apply(2*k,self.lz[k])
        self.all_apply(2*k+1,self.lz[k])
        self.lz[k]=self.identity

    def __init__(self,V,OP,E,MAPPING,COMPOSITION,ID):
        self.n=len(V)
        self.log=(self.n-1).bit_length()
        self.size=1<<self.log
        self.d=[E for i in range(2*self.size)]
        self.lz=[ID for i in range(self.size)]
        self.e=E
        self.op=OP
        self.mapping=MAPPING
        self.composition=COMPOSITION
        self.identity=ID
        for i in range(self.n):self.d[self.size+i]=V[i]
        for i in range(self.size-1,0,-1):self.update(i)

    def set(self,p,x):
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        self.d[p]=x
        for i in range(1,self.log+1):self.update(p>>i)

    def get(self,p):
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        return self.d[p]

    def prod(self,l,r):
        if l==r:return self.e
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self.push(l>>i)
            if (((r>>i)<<i)!=r):self.push(r>>i)
        sml,smr=self.e,self.e
        while(l<r):
            if l&1:
                sml=self.op(sml,self.d[l])
                l+=1
            if r&1:
                r-=1
                smr=self.op(self.d[r],smr)
            l>>=1
            r>>=1
        return self.op(sml,smr)

    def all_prod(self):return self.d[1]

    def apply_point(self,p,f):
        assert 0<=p and p<self.n
        p+=self.size
        for i in range(self.log,0,-1):self.push(p>>i)
        self.d[p]=self.mapping(f,self.d[p])
        for i in range(1,self.log+1):self.update(p>>i)

    def apply(self,l,r,f):
        if l==r:return
        l+=self.size
        r+=self.size
        for i in range(self.log,0,-1):
            if (((l>>i)<<i)!=l):self.push(l>>i)
            if (((r>>i)<<i)!=r):self.push((r-1)>>i)
        l2,r2=l,r
        while(l<r):
            if (l&1):
                self.all_apply(l,f)
                l+=1
            if (r&1):
                r-=1
                self.all_apply(r,f)
            l>>=1
            r>>=1
        l,r=l2,r2
        for i in range(1,self.log+1):
            if (((l>>i)<<i)!=l):self.update(l>>i)
            if (((r>>i)<<i)!=r):self.update((r-1)>>i)

    def max_right(self,l,g):
        if l==self.n:return self.n
        l+=self.size
        for i in range(self.log,0,-1):self.push(l>>i)
        sm=self.e
        while(1):
            while(l%2==0):l>>=1
            if not(g(self.op(sm,self.d[l]))):
                while(l<self.size):
                    self.push(l)
                    l=(2*l)
                    if (g(self.op(sm,self.d[l]))):
                        sm=self.op(sm,self.d[l])
                        l+=1
                return l-self.size
            sm=self.op(sm,self.d[l])
            l+=1
            if (l&-l)==l:break
        return self.n

    def min_left(self,r,g):
        if r==0:return 0
        r+=self.size
        for i in range(self.log,0,-1):self.push((r-1)>>i)
        sm=self.e
        while(1):
            r-=1
            while(r>1 and (r%2)):r>>=1
            if not(g(self.op(self.d[r],sm))):
                while(r<self.size):
                    self.push(r)
                    r=(2*r+1)
                    if g(self.op(self.d[r],sm)):
                        sm=self.op(self.d[r],sm)
                        r-=1
                return r+1-self.size
            sm=self.op(self.d[r],sm)
            if (r&-r)==r:break
        return 0

def is_prime(n):
    if n <= 1:
        return False
    if n == 2 or n == 7 or n == 61:
        return True
    if n % 2 == 0:
        return False
    d = n - 1
    while d % 2 == 0:
        d //= 2
    bases = [2, 7, 61]
    for a in bases:
        t = d
        y = pow_mod(a, t, n)
        while t != n - 1 and y != 1 and y != n - 1:
            y = y * y % n
            t <<= 1
        if y != n - 1 and t % 2 == 0:
            return False
    return True

def primitive_root(m):
    if m == 2:
        return 1
    if m in (167772161, 469762049, 754974721, 998244353):
        return 3
    divs = [2]
    x = (m - 1) // 2
    while x % 2 == 0:
        x //= 2
    i = 3
    while i * i <= x:
        if x % i == 0:
            divs.append(i)
            while x % i == 0:
                x //= i
        i += 2
    if x > 1:
        divs.append(x)

    g = 2
    while True:
        ok = True
        for d in divs:
            if pow_mod(g, (m - 1) // d, m) == 1:
                ok = False
                break
        if ok:
            return g
        g += 1

def zs(s):
    n = len(s)
    ret = [0] * n
    l = 1
    ll = 0
    r = 0
    while l < len(s):
        if l < r:
            ret[l] = min(ret[l-ll],r-l)

        i = ret[l]
        while l+i < len(s) and s[i] == s[l+i]:
            ret[l] +=1

            i +=1
        if ret[l] >0:
            if l+i-1 > r:
                ll = l
                r = max(r,l+i-1)        
        l+=1
    return ret

import heapq
class Solution:
    def minMoves(self, matrix: List[str]) -> int:
        m, n = len(matrix), len(matrix[0])
        ls = matrix[:]

        dd = defaultdict(list)
        for i in range(m):
            for j in range(n):
                c = ls[i][j]
                if 'A' <= c <= 'Z':
                    dd[c].append((i, j))

        INF = float('inf')
        dist = [[INF] * n for i in range(m)]
        dist[0][0] = 0

        pq = [(0, 0, 0)]
        ps = [False] * 26
        dirs = [(1, 0), (-1, 0), (0, 1), (0, -1)]

        while pq:
            d, x, y = heapq.heappop(pq)
            if d > dist[x][y]:
                continue
            if x == m - 1 and y == n - 1:
                break

            c = ls[x][y]
            if 'A' <= c <= 'Z':
                idx = ord(c) - ord('A')
                if not ps[idx]:
                    ps[idx] = True
                    for px, py in dd[c]:
                        if dist[px][py] > d:
                            dist[px][py] = d
                            heapq.heappush(pq, (d, px, py))
                    dd[c].clear()

            for dx, dy in dirs:
                nx, ny = x + dx, y + dy
                if 0 <= nx < m and 0 <= ny < n and ls[nx][ny] != '#':
                    if dist[nx][ny] > d + 1:
                        dist[nx][ny] = d + 1
                        heapq.heappush(pq, (d + 1, nx, ny))

        return -1 if dist[m-1][n-1] == INF else dist[m-1][n-1]

from collections import deque, defaultdict
class Solution:
    def minMoves(self, M: List[str]) -> int:
        m, n, P = len(M), len(M[0]), defaultdict(list)
        for i in range(m):
            for j in range(n):
                c = M[i][j]
                if 'A' <= c <= 'Z': P[c].append((i, j))
        D = [[1e9]*n for _ in range(m)]
        D[0][0], Q, U = 0, deque([(0, 0)]), set()
        while Q:
            x, y = Q.popleft()
            if (x, y) == (m-1, n-1): return D[x][y]
            c = M[x][y]
            if 'A' <= c <= 'Z' and c not in U:
                for a, b in P[c]:
                    if D[a][b] > D[x][y]: D[a][b] = D[x][y]; Q.appendleft((a, b))
                U.add(c)
            for dx, dy in [(-1,0),(1,0),(0,-1),(0,1)]:
                a, b = x+dx, y+dy
                if 0<=a<m and 0<=b<n and M[a][b] != '#' and D[a][b] > D[x][y]+1: D[a][b] = D[x][y]+1; Q.append((a, b))
        return -1

class Solution:
    def minMoves(self, matrix: List[str]) -> int:
        m, n = len(matrix), len(matrix[0])

        portals = defaultdict(list)
        for i in range(m):
            for j in range(n):
                c = matrix[i][j]
                if 'A' <= c <= 'Z':
                    portals[c].append((i, j))

        INF = float('inf')
        dist = [[INF]*n for _ in range(m)]
        if matrix[0][0] == '#':
            return -1
        dist[0][0] = 0

        dq = deque([(0, 0)])
        used_portal = set()


        while dq:
            i, j = dq.popleft()
            d = dist[i][j]

            if i == m-1 and j == n-1:
                return d


            c = matrix[i][j]
            if 'A' <= c <= 'Z' and c not in used_portal:
                used_portal.add(c)
                for ti, tj in portals[c]:
                    if dist[ti][tj] > d:
                        dist[ti][tj] = d
                        dq.appendleft((ti, tj))

            for di, dj in ((1,0),(-1,0),(0,1),(0,-1)):
                ni, nj = i + di, j + dj
                if 0 <= ni < m and 0 <= nj < n and matrix[ni][nj] != '#' and dist[ni][nj] > d+1:
                    dist[ni][nj] = d+1
                    dq.append((ni, nj))

        return -1
