class Solution:
    def countPermutations(self, c: List[int]) -> int:
        mod = 10**9+7
        n = len(c)
        for i in range(1, n):
            if c[i] <= c[0]:
                return 0
        res = 1
        for i in range(1, n):
            res = res * i % mod
        return res

class Solution:
    def countPermutations(self, complexity: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(complexity)

        # check feasibility: comp[0] must be strictly the smallest
        root = complexity[0]
        for x in complexity[1:]:
            if x <= root:
                return 0

        # all other n−1 computers can be unlocked in any order
        res = 1
        for k in range(1, n):
            res = res * k % MOD
        return res

