class Solution:
    def canMakeEqual(self, nums: List[int], k: int) -> bool:
        n = len(nums)
        for t in (1, -1):
            b = [1 if nums[i] != t else 0 for i in range(n)]
            cnt = 0
            for i in range(n - 1):
                if b[i]:
                    cnt += 1
                    b[i] = 0
                    b[i + 1] ^= 1
            if b[-1] == 0 and cnt <= k:
                return True
        return False

class Solution:
    def canMakeEqual(self, nums: List[int], k: int) -> bool:
        def can_t(target:int) -> bool:
            arr= nums[:]
            ops=0
            for i in range(len(arr)-1):
                if arr[i]!=target:
                    arr[i]*=-1
                    arr[i+1]*=-1
                    ops+=1
            return arr[-1]==target and ops<=k
        return can_t(1) or can_t(-1)

class Solution:
    def canMakeEqual(self, nums: List[int], k: int) -> bool:
        n = len(nums)
        for x in (1, -1):
            c = 0
            r = 0

            arr = [0] * (n + 2)
            for i in range(n):
                c += arr[i]

                if nums[i] * (1 if c % 2 == 0 else -1) != x:
                    if i == n - 1 or r == k:
                        break
                    # print(f"i: {i}, c: {c}, r: {r}, arr: {arr}")
                    r += 1
                    c += 1
                    arr[i + 2] -= 1
            else:
                return True

        return False
