import math
from typing import List

class Solution:
    def maximumProduct(self, a: List[int], m: int) -> int:
        n = len(a)
        if m == 1: return max(x*x for x in a)
        x, y = [a[0]]*n, [a[0]]*n
        for i in range(1, n):
            x[i] = max(x[i-1], a[i])
            y[i] = min(y[i-1], a[i])
        r = -math.inf
        for j in range(m-1, n):
            t = a[j]; i = j-m+1
            r = max(r, t*x[i], t*y[i])
        return int(r)

class Solution:
    def maximumProduct(self, a: List[int], m: int) -> int:
        n = len(a)
        if m == 1:
            return max(x*x for x in a)
        curr_max = a[0]
        curr_min = a[0]
        res = -inf
        for j in range(m-1,n):
            if j > m-1:
                x = a[j-m+1]
                if x > curr_max: curr_max = x
                if x < curr_min: curr_min = x
            xj = a[j]
            p1 = curr_max * xj
            p2 = curr_min * xj
            if p1 > res: res = p1
            if p2 > res: res = p2
        return res

