from collections import Counter
class Solution:
    def specialTriplets(self, a: list[int]) -> int:
        m = 10**9+7; n = len(a); l, r = Counter(), Counter(a); s = 0
        for i in range(n):
            r[a[i]] -= 1; d = a[i] * 2
            s += l[d] * r[d]; s %= m
            l[a[i]] += 1
        return s

class Solution:
    def specialTriplets(self, a: List[int]) -> int:
        mod,R,L,res=10**9+7,Counter(a),{},0
        for x in a:
            R[x]-=1
            if R[x]==0: del R[x]
            y=x*2
            res=(res+L.get(y,0)*R.get(y,0))%mod
            L[x]=L.get(x,0)+1
        return res
