class Solution:
    def findCoins(self, w):
        n = len(w)
        dp = [1] + [0] * n
        res = []
        for i in range(1, n + 1):
            c = dp[i]
            d = w[i - 1]
            if c > d:
                return []
            if c < d:
                res.append(i)
                for j in range(i, n + 1):
                    dp[j] += dp[j - i]
        return res if dp[1:] == w else []

class Solution:
    def findCoins(self, numWays: List[int]) -> List[int]:
        l = len(numWays)
        dp = [0] * (l + 1)
        dp[0] = 1
        sa = []
        for d in range(1, l + 1):
            if dp[d] < numWays[d - 1]:
                if numWays[d - 1] - dp[d] != 1:
                    return []
                sa.append(d)
                for i in range(d, l + 1):
                    dp[i] += dp[i - d]
            if dp[d] > numWays[d - 1]:
                return []
        for i in range(1, l + 1):
            if dp[i] != numWays[i - 1]:
                return []
        return sa

