class Solution:
    def findCoins(self, numWays: List[int]) -> List[int]:
        n = len(numWays)
        dp = [0]*(n+1)
        dp[0] = 1
        coins = [] # we will build this answer

        for val in range(1, n+1):
            cur = dp[val]
            need = numWays[val-1]

            # already matches
            if cur == need:
                continue

            # fix deficit of exactly 1
            if need - cur == 1:
                coins.append(val)

                for x in range(val, n+1):
                    dp[x] += dp[x-val]

                if dp[val] != need:
                    return[]
            else:
                return[]
        return coins
