class Solution:
    def validateCoupons(self, C: List[str], B: List[str], A: List[bool]) -> List[str]:
        m = {'electronics':0,'grocery':1,'pharmacy':2,'restaurant':3}
        p,x=re.compile(r'^[A-Za-z0-9_]+$'), []
        for c,b,a in zip(C,B,A):
            if a and b in m and c and p.match(c):
                x.append((m[b],c))
        x.sort()
        return [c for _,c in x]

class Solution:
    def validateCoupons(self, code: List[str], businessLine: List[str], isActive: List[bool]) -> List[str]:
        order = {
            "electronics": 0,
            "grocery":     1,
            "pharmacy":    2,
            "restaurant":  3
        }
        valid = []
        for c, b, active in zip(code, businessLine, isActive):
            if not active:
                continue
            if not c:
                continue
            if not all(ch.isalnum() or ch == "_" for ch in c):
                continue
            if b not in order:
                continue
            valid.append((order[b], c))
        valid.sort(key=lambda x: (x[0], x[1]))
        return [c for _, c in valid]
