class Solution:
    def minCost(self, n: int, edges: List[List[int]], k: int) -> int:
        if k >= n:
            return 0
        parent = list(range(n))
        def find(x):
            while parent[x] != x:
                parent[x] = parent[parent[x]]
                x = parent[x]
            return x
        def union(x, y):
            rx, ry = find(x), find(y)
            if rx != ry:
                parent[ry] = rx
        weights = sorted({w for _, _, w in edges})
        ans = weights[-1]
        lo, hi = 0, len(weights) - 1
        while lo <= hi:
            mid = (lo + hi) // 2
            thresh = weights[mid]
            for i in range(n):
                parent[i] = i
            for u, v, w in edges:
                if w <= thresh:
                    union(u, v)
            comps = len({find(i) for i in range(n)})
            if comps <= k:
                ans = thresh
                hi = mid - 1
            else:
                lo = mid + 1
        return ans

