class Solution:
    def isTrionic(self, a: list[int]) -> bool:
        n = len(a)
        if n < 3:
            return False
        p = 0
        f1 = f2 = f3 = False
        for i in range(1, n):
            u, v = a[i - 1], a[i]
            if v == u:
                return False
            if p == 0:
                if v > u:
                    f1 = True
                else:
                    if not f1:
                        return False
                    p = 1
                    f2 = True
            elif p == 1:
                if v < u:
                    f2 = True
                else:
                    if not f2:
                        return False
                    p = 2
                    f3 = True
            else:
                if v > u:
                    f3 = True
                else:
                    return False
        return p == 2 and f1 and f2 and f3

class Solution:
    def isTrionic(self, nums: List[int]) -> bool:
        n = len(nums)
        for p in range(1, n - 1):
            for q in range(p + 1, n - 1):
                inc1 = all(nums[i] < nums[i + 1] for i in range(p))
                dec = all(nums[i] > nums[i + 1] for i in range(p, q))
                inc2 = all(nums[i] < nums[i + 1] for i in range(q, n - 1))
                if inc1 and dec and inc2:
                    return True
        return False

class Solution:
    def isTrionic(self, nums: List[int]) -> bool:
        prev = -1
        for i in range(1, len(nums)):
            a = nums[i-1]
            b = nums[i]
            if a == b:
                return False
            if a > b:
                if i == 1:
                    return False
                if prev != -1 and prev != i-1:
                    return False
                prev = i
        return prev != -1 and prev < len(nums) - 1
