class Solution:
    def maxBalancedShipments(self, w: List[int]) -> int:
        n, a, b = len(w), 0, w[0]
        for i in range(1, n):
            if w[i] > b: b = w[i]
            if w[i] < b:
                a += 1
                if i + 1 < n: b = w[i + 1]
        return a

class Solution:
    def maxBalancedShipments(self, weight: List[int]) -> int:
        n = len(weight)
        count = 0
        i = 0

        while i < n - 1:
            currMax = weight[i]
            found = False
            for j in range(i + 1, n):
                if weight[j] > currMax:
                    currMax = weight[j]
                if weight[j] < currMax:
                    count += 1
                    i = j + 1
                    found = True
                    break
            if not found:
                break

        return count

class Solution:
    def maxBalancedShipments(self, weight: List[int]) -> int:
        ans = 0
        high = 0
        for x in weight:
            if x < high:
                ans += 1
                high = 0
            else:
                high = max(high, x)
        return ans
