class Solution:
    def sortPermutation(self, nums: List[int]) -> int:
        n = len(nums)
        if all(i == v for i, v in enumerate(nums)):
            return 0

        arr = [0] * n
        for i, j in enumerate(nums):
            arr[j] = i
        # print(arr)
        ans = (1 << (n - 1).bit_length()) - 1
        # print(ans)

        for j in range(n):
            if arr[j] != j:
                ans &= j

        return ans
