class Solution:
    def reverseSubmatrix(self, g: List[List[int]], a: int, b: int, s: int) -> List[List[int]]:
        r, c, k = a, b, s
        tmp = [g[r + i][c:c + k] for i in range(k)]
        for i, v in enumerate(reversed(tmp)):
            g[r + i][c:c + k] = v
        return g

class Solution:
    def reverseSubmatrix(self, grid: List[List[int]], x: int, y: int, k: int) -> List[List[int]]:
        for i in range(k//2):
            for j in range(y,y+k):
                grid[x+i][j],grid[x+k-1-i][j] = grid[x+k-1-i][j],grid[x+i][j]
        return grid

class Solution:
    def reverseSubmatrix(self, grid: List[List[int]], x: int, y: int, k: int) -> List[List[int]]:
        for i in range(k // 2):
            m = x + i
            n = x + k - 1 - i
            grid[m][y:y + k], grid[n][y:y + k] = grid[n][y:y + k], grid[m][y:y + k]
        return grid
