class Solution:
    def minArraySum(self, nums: List[int], k: int) -> int:
        s = 0
        dp = 0
        best = {0: 0}
        total = 0
        for x in nums:
            total += x
            s += x
            r = s%k
            dp = max(dp, best.get(r, float("-inf")) + s)
            v = dp -s
            if best.get(r, float("-inf")) < v:
                best[r] = v
        return total - dp
