from collections import Counter

class Solution:
    def partitionArray(self, nums: List[int], k: int) -> bool:
        n = len(nums)
        if n % k != 0:
            return False

        groups = n // k
        freq = Counter(nums)

        return all(count <= groups for count in freq.values())

