class Solution:
    def maxValue(self, nums: List[int]) -> List[int]:
        n = len(nums)
        if n == 0:
            return []

        pref = [0] * n
        smin = [0] * n

        cur = float("-inf")
        for i in range(n):
            cur = max(cur, nums[i])
            pref[i] = cur

        cur = float("inf")
        for i in range(n - 1, -1, -1):
            cur = min(cur, nums[i])
            smin[i] = cur

        ans = [0] * n
        start = 0
        for i in range(n - 1):
            if pref[i] <= smin[i + 1]:
                segMax = pref[i]
                for j in range(start, i + 1):
                    ans[j] = segMax
                start = i + 1

        segMax = pref[n - 1]
        for j in range(start, n):
            ans[j] = segMax

        return ans
