class Solution:
    def recoverOrder(self, order: List[int], friends: List[int]) -> List[int]:
        p = {v:i for i,v in enumerate(order)}
        return sorted(friends, key=lambda x: p[x])

class Solution:
    def recoverOrder(self, order: List[int], friends: List[int]) -> List[int]:
        f = set(friends)
        ans = []
        for o in order:
            if o in f:
               ans.append(o) 
        return ans

class Solution:
    def recoverOrder(self, a: List[int], b: List[int]) -> List[int]:
        s = set(b)
        r = []
        for x in a:
            if x in s:
                r.append(x)
        return r
