class Solution:
    def bowlSubarrays(self, nums: List[int]) -> int:
        par=nums[:]


        n=len(par)
        pi=[-1]*n
        ni=[-1]*n
        st=[]

        for i,x in enumerate(par):
            while st and par[st[-1]]<x:
                st.pop()
            pi[i]=st[-1] if st else -1
            st.append(i)

        st=[]

        for i in range(n-1,-1,-1):
            x=par[i]
            while st and par[st[-1]]<x:
                st.pop()
            ni[i]=st[-1] if st else n
            st.append(i)

        ans=0
        for i in range(n):
            if pi[i]!=-1 and i-pi[i]>=2:
                ans+=1
            if ni[i]!=n and ni[i]-i>=2:
                ans+=1

        return ans

class Solution:
    def bowlSubarrays(self, nums: List[int]) -> int:
        n = len(nums)
        if n < 3:
            return 0
        st = []
        vis = 0
        for x in nums:
            while st and st[-1] < x:
                st.pop()
                vis += 1
            if st:
                vis += 1
            st.append(x)
        return vis - (n - 1)
