        MOD = 10**9 + 7
        res = 0

        @cache
        def dp(i, pp, p):
            if i == len(nums):
                return 1
            ifSkip = dp(i + 1, pp, p)
            v = nums[i]
            if v % 2 == pp == p:
                return ifSkip
            ifTake = dp(i + 1, p, v % 2)
            return (ifSkip + ifTake) % MOD

        res = dp(0, -1, -1)
        dp.cache_clear()
        return res - 1

