  def countStableSubsequences(self, nums: List[int]) -> int:
    MOD = 1_000_000_007
    E1 = E2 = O1 = O2 = 0

    for a in nums:
        if a % 2 == 0:
            newE1 = (E1 + O1 + O2 + 1) % MOD
            newE2 = (E2 + E1) % MOD
            E1, E2 = newE1, newE2
        else:
            newO1 = (O1 + E1 + E2 + 1) % MOD
            newO2 = (O2 + O1) % MOD
            O1, O2 = newO1, newO2
    return (E1 + E2 + O1 + O2) % MOD

class Solution:
    def countStableSubsequences(self, nums: List[int]) -> int:
        modulo = 10**9 + 7

        nums = [x%2 for x in nums]

        @lru_cache(None)
        def recur_fn(i,prev,count):
            if i == len(nums):
                return 0
            else:
                res = 0
                if nums[i] == prev and count == 1:
                    res += 1 + recur_fn(i+1,prev,count+1)
                    if res >= modulo:
                        res %= modulo
                if nums[i] != prev:
                    res += 1+ recur_fn(i+1,nums[i],1)
                    if res >= modulo:
                        res %= modulo
                res += recur_fn(i+1,prev,count)
                if res >= modulo:
                    res %= modulo
                return res 

        ans = 0
        for i in range(len(nums)):
            ans += 1 + recur_fn(i+1,nums[i],1)
            #print('ans',ans)
            if ans >= modulo:
                ans %= modulo

        recur_fn.cache_clear()
        return ans%modulo
