class Solution:
    def maxKDistinct(self, nums: List[int], k: int) -> List[int]:
        res = []
        nums.sort(reverse=True)
        seen = set()
        taken = 0
        for i, v in enumerate(nums):
            if taken == k:
                break
            if v in seen:
                continue
            seen.add(v)
            res.append(v)
            taken += 1

        return res

