def last(lst):
    if lst is None:
        raise TypeError("lst is None")
    try:
        n = len(lst)
    except Exception:
        n = None
    if n is not None:
        if n == 0:
            raise IndexError("empty")
        try:
            return lst[n - 1]
        except Exception:
            pass
    sentinel = object()
    result = sentinel
    for item in lst:
        result = item
    if result is sentinel:
        raise IndexError("empty")
    return result

def prepend_item(lst, value):
    result = [value]
    for i in range(len(lst)):
        result.append(lst[i])
    return result

def map_negate(lst):
    out = []
    for i in range(len(lst)):
        val = lst[i]
        out.append(-val)
    return out

def clamp(lst, low, high):
    if low > high:
        raise ValueError("low cannot be greater than high")
    result = []
    for x in lst:
        v = x
        if v < low:
            v = low
        elif v > high:
            v = high
        result.append(v)
    return result

def max_value(lst):
    if not lst:
        raise ValueError("max_value() arg is an empty sequence")
    m = lst[0]
    i = 1
    n = len(lst)
    while i < n:
        v = lst[i]
        if v > m:
            m = v
        i += 1
    return m

def kth_smallest(lst, k):
    def select(arr, k):
        n = len(arr)
        if n <= 5:
            a = arr[:]
            a.sort()
            return a[k]
        groups = []
        for i in range(0, n, 5):
            g = arr[i:i+5]
            g.sort()
            groups.append(g[len(g)//2])
        pivot = select(groups, len(groups)//2)
        lows = []
        highs = []
        pivots = []
        for x in arr:
            if x < pivot:
                lows.append(x)
            elif x > pivot:
                highs.append(x)
            else:
                pivots.append(x)
        if k < len(lows):
            return select(lows, k)
        elif k < len(lows) + len(pivots):
            return pivot
        else:
            return select(highs, k - len(lows) - len(pivots))
    return select(lst, k)

def counting_sort(lst):
    n = len(lst)
    if n == 0:
        return []
    m = lst[0]
    for i in range(1, n):
        v = lst[i]
        if v > m:
            m = v
    counts = [0] * (m + 1)
    for v in lst:
        if v < 0:
            raise ValueError("All values must be non-negative")
        counts[v] += 1
    result = [0] * n
    idx = 0
    for value in range(len(counts)):
        c = counts[value]
        for _ in range(c):
            result[idx] = value
            idx += 1
    return result
